INSERT INTO `tbl_books` (`id`, `cat_id`, `aid`, `featured`, `book_title`, `book_description`, `book_cover_img`, `book_bg_img`, `total_rate`, `rate_avg`, `book_views`, `book_chapter_status`, `status`) VALUES
(1, 5, 1, 0, 'Tiên Nghịch', '<p><strong><strong>Ti&ecirc;n Nghịch</strong>&nbsp;l&agrave; c&acirc;u chuyện Ti&ecirc;n Hiệp kể về Vương L&acirc;m - một thiếu ni&ecirc;n b&igrave;nh thường, may mắn được gia nhập v&agrave;o một m&ocirc;n ph&aacute;i&nbsp;<em>tu ti&ecirc;n</em>&nbsp;xuống dốc của nước Triệu, v&igrave; thiếu linh căn, v&igrave; một hiểu nhầm tai hại, v&igrave; một khối thiết tinh v&agrave; nhờ c&oacute; được một &quot;Thần B&iacute; Hạt Ch&acirc;u&quot;. Vương L&acirc;m đ&atilde; bước l&ecirc;n con đường tu ti&ecirc;n v&agrave; tr&ecirc;n con đường n&agrave;y, hắn sẽ đối mặt với chuyện g&igrave;?</strong><br />\r\n<br />\r\n- Tu ti&ecirc;n l&agrave; nghịch thi&ecirc;n hay thuận thi&ecirc;n, l&agrave; v&ocirc; t&igrave;nh hay hữu t&igrave;nh? H&atilde;y c&ugrave;ng d&otilde;i theo bước ch&acirc;n của Vương L&acirc;m qua 1976 chương Truyện Ti&ecirc;n Nghịch&gt; để biết r&otilde; những điều n&agrave;y bạn nh&eacute;!<br />\r\n&nbsp;</p>\r\n', '80258_tien-nghich.png', '24215_tien-nghich.png', 0, '0', 29, 0, 1),
(2, 1, 1, 0, 'Cầu Ma', '<p>Nếu như c&aacute;c bạn đ&atilde; từng l&agrave; fan đọc của Ti&ecirc;n Nghịch, hay từng đọc qua bộ Ti&ecirc;n Nghịch, chắc c&aacute;c bạn kh&ocirc;ng thể n&agrave;o qu&ecirc;n được t&aacute;c giả Nhĩ Căn, một t&aacute;c giả tuy mới nhưng b&uacute;t lực c&oacute; thể n&oacute;i l&agrave; v&ocirc; c&ugrave;ng vững chắc.</p>\r\n\r\n<p>Qua nhiều ng&agrave;y mong đợi, cuối c&ugrave;ng Nhĩ Căn đ&atilde; cho ra t&aacute;c phẩm mới: Cầu Ma.<br />\r\nNhư thế n&agrave;o l&agrave; ma? L&agrave; người t&aacute;ng tận lương t&acirc;m, v&igrave; th&agrave;nh c&ocirc;ng m&agrave; kh&ocirc;ng từ thủ đoạn, thậm ch&iacute; tu h&agrave;nh ma c&ocirc;ng để th&agrave;nh ma đầu sao?</p>\r\n\r\n<p>Nhĩ Căn kh&ocirc;ng nghĩ thế, Nhĩ Căn muốn viết về một MA thật sự, chứ kh&ocirc;ng phải l&agrave; một ma đầu th&ocirc; tục &acirc;m u.</p>\r\n\r\n<p>Với t&aacute;c phẩm n&agrave;y, Nhĩ Căn mong muốn truyền đạt đến cho người đọc cuộc sống đầy nh&acirc;n sinh, một nội dung động l&ograve;ng người.</p>\r\n', '9773_cau-ma.jpg', '60895_cau-ma.jpg', 3, '4', 284, 0, 1),
(3, 1, 1, 0, 'Nhất Niệm Vĩnh Hằng', '<p>Nhất niệm th&agrave;nh biển cả, nhất niệm h&oacute;a nương d&acirc;u.<br />\r\nNhất niệm trảm ngh&igrave;n Ma, nhất niệm giết vạn Ti&ecirc;n.<br />\r\n<br />\r\nChỉ c&oacute; niệm của ta... l&agrave; Vĩnh hằng.</p>\r\n', '53456_nhat-niem-vinh-hang.jpg', '63522_nhat-niem-vinh-hang.jpg', 0, '0', 3, 0, 1),
(4, 5, 1, 1, 'Ngã Dục Phong Thiên', '<p>Nếu ta muốn c&oacute;, thi&ecirc;n kh&ocirc;ng thể kh&ocirc;ng.<br />\r\nNếu ta muốn kh&ocirc;ng, thi&ecirc;n kh&ocirc;ng được c&oacute;.<br />\r\n<br />\r\nChỉ hai c&acirc;u văn &aacute;n đ&atilde; đủ l&ecirc;n sự b&aacute; đạo của nh&acirc;n vật ch&iacute;nh!</p>\r\n', '39121_nga-duc-phong-thien.jpg', '28015_nga-duc-phong-thien.jpg', 3, '3', 678, 0, 1),
(5, 6, 2, 0, 'Thần Ma Hệ Thống', '<p>Truyện Thần Ma Hệ Thống l&agrave; truyện đầy t&iacute;nh huyễn hoặc v&agrave; kh&ocirc;ng gian mộng ảo. Kh&ocirc;ng gian n&agrave;y thường thấy ở những truyện cũng thể loại, c&oacute; ch&uacute;t thực những lại c&oacute; ch&uacute;t hư v&ocirc;, ảo ảnh; để h&ograve;a nhập v&agrave; theo d&otilde;i đến c&ugrave;ng bạn đọc h&atilde;y đi theo từng chương, từng chương truyện.rn<br />\r\n<br />\r\nChỉ trong nh&aacute;y mắt thế giới bỗng nhi&ecirc;n biến th&agrave;nh ng&agrave;y tận thế. Tang thi, y&ecirc;u th&uacute; biến dị ho&agrave;nh h&agrave;nh thế giới, trong khi đ&oacute; nh&acirc;n loại rời v&agrave;o cảnh đau khổ gi&atilde;y dụa cần sinh. Khi Nhạc Trọng c&ugrave;ng qu&acirc;n đội của hắn trải qua v&ocirc; số kh&oacute; khăn cuối c&ugrave;ng từ trong tang thi v&agrave; biến dị th&uacute; thu phục cả nước l&agrave; l&uacute;c, hắn mới sợ h&atilde;i ph&aacute;t hiện, hắn chỉ mới x&ocirc;ng qua cửa thứ nhất.rn<br />\r\n<br />\r\nThế giới tr&agrave;n ngập bức xạ hạt nh&acirc;n, biến dị nh&acirc;n, bức xạ th&uacute;, vật tư cực độ thiếu hụt, những sinh vật thời tiền sử ho&agrave;nh h&agrave;nh, khủng long đ&atilde; tiến ho&aacute; ra tr&iacute; tuệ chiếm cứ địa vị thống trị thế giới, xuất hiện nhiều chủng tộc cường đại sinh ra dị năng, tố chất th&acirc;n thể tốt hơn nh&acirc;n loại gấp mười lần, vi&ecirc;n đạn b&igrave;nh thường kh&ocirc;ng xuy&ecirc;n qua nổi th&acirc;n thể ch&uacute;ng. Th&uacute; nh&acirc;n trở th&agrave;nh kẻ thống trị n&ocirc; dịch l&ecirc;n thế giới nh&acirc;n loại, khi to&agrave;n bộ nh&acirc;n loại bị v&acirc;y trong vị tr&iacute; tuyệt đối nhược thế, Nhạc Trọng lại phải tiếp tục l&agrave;m cho m&igrave;nh tiến h&oacute;a, tiến h&oacute;a kh&ocirc;ng ngừng, c&ugrave;ng người th&acirc;n, c&ugrave;ng thật nhiều hồng nhan tri kỷ, c&ugrave;ng bạn b&egrave; đo&agrave;n thể gian nan t&igrave;m kiếm một tiền đồ quang minh, hướng thẳng về tinh kh&ocirc;ng vũ trụ, đưa nh&acirc;n loại đi l&ecirc;n một con đường ph&aacute;t triển rộng lớn hơn, đi v&agrave;o một thế giới bao la hạo h&atilde;n của tương lai mới&hellip;rn<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một truyện huyền huyễn với t&igrave;nh tiết truyện gay cấn, dồn dập từ đầu cho đến cuối kh&ocirc;ng c&oacute; &quot;đoạn lặng&quot;, kh&ocirc;ng hề c&oacute; t&igrave;nh tiết c&acirc;u chữ. T&aacute;c giả tạo cho độc giả cảm gi&aacute;c như m&igrave;nh bị cuốn v&agrave;o c&acirc;u chuyện, như đang chiến đấu với Tang Thi, đuổi giết c&ugrave;ng Dị Th&uacute;...&nbsp;người đọc phải mải m&ecirc; chinh phục h&agrave;nh tr&igrave;nh v&agrave; đo&aacute;n b&aacute;o tương lai.</p>\r\n', '87249_truyen-than-ma-he-thong.jpg', '39891_truyen-than-ma-he-thong.jpg', 0, '0', 1, 0, 1),
(6, 4, 6, 0, 'Chưởng Thượng Kiều', '<p><strong>Độ d&agrave;i</strong>: 109 chương<br />\r\n<strong>T&igrave;nh trạng:</strong>&nbsp;Ho&agrave;n (Bản gốc) - On going (Bản dịch)<br />\r\n<strong>Thể loại</strong>: Hiện đại, gương vỡ lại l&agrave;nh, 3 kiếp lu&acirc;n hồi, ngọt, ngược, nam ch&iacute;nh th&acirc;m t&igrave;nh, HE.<br />\r\n<strong>CP ch&iacute;nh:</strong>&nbsp;Hướng Tinh Bắc&nbsp; x Ch&acirc;n Chu&nbsp;<br />\r\n<br />\r\nC&ocirc; v&agrave; anh đ&atilde; ở b&ecirc;n nhau mười năm... Ng&agrave;y kỉ niệm mười năm đ&oacute;, c&ocirc; bước l&ecirc;n chiến hạm Viễn Dương, l&ecirc;nh đ&ecirc;nh tr&ecirc;n những con s&oacute;ng. Nửa th&aacute;ng sau, c&ocirc; đưa cho anh đơn ly h&ocirc;n.<br />\r\n<br />\r\nSau đ&oacute; c&ocirc; xuất ngoại, ng&agrave;y tới phi trường, c&ocirc; mới biết tin, rằng anh gặp bất trắc khi l&agrave;m nhiệm vụ...<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng của c&ocirc; đ&atilde; ở dưới biển s&acirc;u, mang theo những k&iacute; ức về t&igrave;nh y&ecirc;u của hai người, m&atilde;i m&atilde;i ch&ocirc;n v&ugrave;i dưới đ&aacute;y biển.<br />\r\n<br />\r\nNếu như c&oacute; cơ hội l&agrave;m lại, lục đạo lu&acirc;n hồi, đời đời kiếp kiếp, c&ocirc; sẽ phải t&igrave;m c&aacute;ch khiến anh y&ecirc;u c&ocirc;, cam t&acirc;m t&igrave;nh nguyện chết v&igrave; c&ocirc;, c&ocirc; sẽ c&oacute; được cơ hội cứu anh ở kiếp n&agrave;y. Nhưng nếu kh&ocirc;ng th&agrave;nh c&ocirc;ng, c&ocirc; sẽ phải trả gi&aacute; bằng cả mạng sống của m&igrave;nh. C&ocirc; c&oacute; đồng &yacute; hay kh&ocirc;ng?&nbsp;<br />\r\n<br />\r\nCh&acirc;n Chu kh&ocirc;ng ch&uacute;t do dự gật đầu, bất kể Hướng Tinh Bắc biến th&agrave;nh c&aacute;i g&igrave;, c&ocirc; cũng sẽ mang anh trở về b&ecirc;n m&igrave;nh một lần nữa.</p>\r\n', '77462_chuong-thuong-kieu.jpg', '76835_chuong-thuong-kieu.jpg', 0, '0', 0, 0, 1),
(7, 4, 6, 0, 'Trở lại 30 Năm Trước', '<p>An Na l&agrave; một thiếu nữ được cưng chiều từ b&eacute; đến lớn Bố c&ocirc; trước đ&acirc;y l&agrave; bộ đội, sau n&agrave;y chuyển hướng l&agrave;m kinh doanh, may sao cũng kh&aacute; th&agrave;nh c&ocirc;ng. Mẹ c&ocirc; l&uacute;c trẻ l&agrave; một thiếu nữ văn nghệ, từng rất say m&ecirc; t&aacute;c phẩm kinh điển của t&aacute;c giả Karenina, v&igrave; vậy mới quyết định sau n&agrave;y c&oacute; con g&aacute;i, b&agrave; sẽ đặt t&ecirc;n con như tựa đề t&aacute;c phẩm đ&oacute; vậy...<br />\r\n<br />\r\nL&uacute;c trước, ba mẹ An Na vốn c&oacute; một cậu con trai, nhưng lại bất hạnh qua đời sớm, hai l&atilde;o đến tuổi trung ni&ecirc;n mới c&oacute; được An Na. C&ocirc; kh&ocirc;ng chỉ l&agrave; con một m&agrave; c&ograve;n l&agrave; đứa con l&uacute;c tuổi hạc, kh&ocirc;ng y&ecirc;u chiều bảo bọc c&ocirc; sao được!?</p>\r\n', '46673_tro-lai-30-nam-truoc.jpg', '9213_tro-lai-30-nam-truoc.jpg', 0, '0', 0, 0, 1),
(8, 4, 6, 1, 'Quyến Luyến Phù Thành', '<p>Ch&uacute;ng ta l&agrave; duy&ecirc;n trời t&aacute;c hợp<br />\r\n<br />\r\nNhiếp T&aacute;i Trầm v&agrave; Bạch Cẩm T&uacute;, một người vốn c&oacute; thể trở th&agrave;nh Quan đới tứ phẩm&nbsp;lại tới l&agrave;m t&agrave;i xế cho Bạch thi&ecirc;n kim, Bạch gia tiểu thư Bạch Cẩm T&uacute; du học trở về, muốn tr&aacute;nh h&ocirc;n nhưng kh&ocirc;ng đ&agrave;nh l&ograve;ng cha mẹ, v&igrave; thế cũng chấp nhận sự quan t&acirc;m từ&nbsp;gia đ&igrave;nh,chấp nhận t&agrave;i xế tư nh&acirc;n<br />\r\n<br />\r\nAnh:<br />\r\n<br />\r\n&ndash; L&uacute;c trước anh y&ecirc;u tiểu thư Bạch Gia, nhưng c&ocirc; ấy lại ch&ecirc; anh kh&ocirc;ng th&egrave;m để &yacute; tới anh.<br />\r\n<br />\r\nN&agrave;ng tỏ vẻ rất giận:<br />\r\n<br />\r\n&ndash;&nbsp; Thật qu&aacute; đ&aacute;ng! Tr&ecirc;n đời sao lại c&oacute; người xấu nết như vậy thế!<br />\r\n<br />\r\nAnh:<br />\r\n<br />\r\n&ndash; Hết c&aacute;ch th&ocirc;i, ai bảo c&ocirc; ấy qu&aacute; xinh đẹp quyến rũ, c&oacute; thể l&agrave;m thần tử dưới v&aacute;y c&ocirc; ấy, l&agrave; may mắn của anh.<br />\r\n<br />\r\nN&agrave;ng n&eacute;n cười:<br />\r\n<br />\r\n&ndash; Vậy l&uacute;c ấy anh đang l&agrave;m g&igrave;, tướng qu&acirc;n của em?<br />\r\n<br />\r\nAnh:<br />\r\n<br />\r\n&ndash; Anh l&agrave;m t&agrave;i xế cho c&ocirc; ấy.<br />\r\n<br />\r\nAnh nh&igrave;n c&ocirc; chăm ch&uacute;, mỉm cười n&oacute;i.<br />\r\n<br />\r\n&hellip;&hellip;&hellip;</p>\r\n', '96992_quyen-luyen-phu-thanh.jpg', '31832_quyen-luyen-phu-thanh.jpg', 0, '0', 20, 0, 1),
(9, 7, 7, 0, 'The Gamer Hệ Thống', '<p>Hắn l&agrave; một sinh vi&ecirc;n mới rời khỏi ghế nh&agrave; trường chưa c&oacute; c&ocirc;ng ăn việc l&agrave;m n&ecirc;n ở nh&agrave; ăn b&aacute;m bố mẹ.<br />\r\n<br />\r\nBỗng một ng&agrave;y hắn đột nhi&ecirc;n trở về năm 2010, trở lại th&agrave;nh một t&ecirc;n học sinh lớp mười.<br />\r\n<br />\r\nHắn vốn nghĩ m&igrave;nh phải khổ sở lăn lộn tr&ecirc;n ghế nh&agrave; trường mấy năm nữa th&igrave; đột nhi&ecirc;n ph&aacute;t hiện ra tr&ecirc;n đầu người kh&aacute;c hiện ra họ t&ecirc;n của họ v&agrave; k&egrave;m theo lv...<br />\r\n<br />\r\n<strong>Đ&ocirc;i lời t&aacute;c giả:</strong><br />\r\n<br />\r\nPs: truyện n&agrave;y m&igrave;nh lấy &yacute; tưởng từ bộ truyện the gamer của h&agrave;n n&ecirc;n nếu ai bảo m&igrave;nh ăn cắp &yacute; tưởng th&igrave;... đ&uacute;ng rồi đấy, gọi c&ocirc;ng an bắt m&igrave;nh đi /cdeu<br />\r\nPs2: m&igrave;nh viết bằng điện thoại n&ecirc;n chắc mỗi ng&agrave;y chỉ đăng 1-2 chương th&ocirc;i<br />\r\nPs3: bộ truyện n&agrave;y ho&agrave;n to&agrave;n l&agrave; hư cấu, kh&ocirc;ng mang bất cứ yếu tố ch&acirc;n thực n&agrave;o v&agrave; kh&ocirc;ng c&oacute; &yacute; định chỉ tr&iacute;ch hay x&uacute;c phạm đến bất kỳ tổ chức, đo&agrave;n thể hay c&aacute; nh&acirc;n n&agrave;o cụ thể. T&aacute;c giả sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm với bất kỳ h&agrave;nh vi c&aacute; nh&acirc;n n&agrave;o của độc giả n&ecirc;n h&atilde;y suy nghĩ thật kỹ để c&oacute; thể ph&acirc;n biệt được giữa 1 bộ truyện giả tưởng v&agrave; đời thực.</p>\r\n', '86339_the-gamer-he-thong.jpg', '44008_the-gamer-he-thong.jpg', 0, '0', 1, 0, 1),
(10, 2, 3, 0, 'Phàm Nhân Tu Tiên ', '<p>Ph&agrave;m Nh&acirc;n Tu Ti&ecirc;n l&agrave; một c&acirc;u chuyện&nbsp;<em>Ti&ecirc;n Hiệp</em>&nbsp;kể về H&agrave;n Lập - Một người b&igrave;nh thường nhưng lại gặp v&ocirc; v&agrave;n cơ duy&ecirc;n để bước đi tr&ecirc;n con đường tu ti&ecirc;n, kh&ocirc;ng phải anh h&ugrave;ng - cũng chẳng phải tiểu nh&acirc;n, H&agrave;n Lập từng bước khẳng định m&igrave;nh... Liệu H&agrave;n Lập v&agrave; người y&ecirc;u c&oacute; thể c&ugrave;ng bước tr&ecirc;n con đường tu ti&ecirc;n v&agrave; c&oacute; một c&aacute;i kết ho&agrave;n mỹ? Những thử th&aacute;ch n&agrave;o đang chờ đợi bọn họ?<br />\r\n<br />\r\n- Truyện kết cấu kh&aacute; hợp l&yacute;, t&igrave;nh tiết kh&ocirc;ng qu&aacute; chậm, kh&ocirc;ng qu&aacute; nhanh, diễn tả kh&aacute; đặc biệt, lời văn tr&ocirc;i chảy, nh&acirc;n vật t&iacute;nh c&aacute;ch đặc th&ugrave;. Nh&acirc;n vật ch&iacute;nh, H&agrave;n Lập, mang hơi hướng kh&aacute; c&ocirc; độc. Bạn n&agrave;o th&iacute;ch đọc Tru Ti&ecirc;n hoặc Thương Thi&ecirc;n chắc sẽ th&iacute;ch&nbsp;<em>Ph&agrave;m Nh&acirc;n Tu Ti&ecirc;n Truyện</em>. Mời bạn đọc c&ugrave;ng thưởng thức v&agrave; d&otilde;i theo bước ch&acirc;n của H&agrave;n Lập!<br />\r\n<br />\r\n- Tags: pham nhan tu tien prc full, truyen tien hiep</p>\r\n', '44207_pham-nhan-tu-tien.jpg', '98484_pham-nhan-tu-tien.jpg', 0, '0', 19, 0, 1),
(11, 2, 3, 0, 'Phàm Nhân Tu Tiên 2', '<p><em>Ph&agrave;m nh&acirc;n tu ti&ecirc;n, phong v&acirc;n t&aacute;i khởi<br />\r\nXuy&ecirc;n qua kh&ocirc;ng gian v&agrave; thời gian, lu&acirc;n hồi nghịch chuyển<br />\r\nKim Ti&ecirc;n Th&aacute;i Ất, Đại La Đạo Tổ Tam thi&ecirc;n đại đạo, ph&aacute;p tắc ch&iacute; t&ocirc;n.</em><br />\r\n<br />\r\n《 Ph&agrave;m Nh&acirc;n Tu Ti&ecirc;n Truyện 》 Ti&ecirc;n Giới Thi&ecirc;n l&agrave; t&iacute;ch xưa về một H&agrave;n Lập khuynh đảo Ti&ecirc;n Giới, một tiểu tử người ph&agrave;m tu ti&ecirc;n đến cảnh giới Bất Diệt trong truyền thuyết.</p>\r\n', '42222_pham-nhan-tu-tien-2.jpg', '66087_pham-nhan-tu-tien-2.jpg', 1, '5', 3, 0, 1),
(12, 1, 3, 1, 'Ma Thiên Ký', '<p><strong><em>Dịch:</em></strong><em>&nbsp;Ph&agrave;m Nh&acirc;n T&ocirc;ng, Flame Haze</em><br />\r\n<br />\r\n- Một thiếu ni&ecirc;n lớn l&ecirc;n trong một nơi người ăn thịt người, học được ch&uacute;t &iacute;t b&iacute; kỹ đ&aacute;nh đấm. Trong một lần Hung đảo nơi hắn lớn l&ecirc;n trong một đ&ecirc;m kh&ocirc;ng biết v&igrave; sao biến mất dưới đ&aacute;y biển, hắn tho&aacute;t ra khỏi đ&oacute; v&agrave; bị người truy đuổi. Sau khi chạy kh&ocirc;ng tho&aacute;t cảnh bị đuổi giết, thiếu ni&ecirc;n đ&oacute; ng&atilde; xuống ...<br />\r\n<br />\r\n- Nhưng kết th&uacute;c l&agrave; một khởi đầu mới, nhờ cơ duy&ecirc;n xảo hợp hắn thay đổi được th&acirc;n phận, được bước v&agrave;o trong một T&ocirc;ng m&ocirc;n tu luyện. Từ đ&oacute; về sau, người đ&atilde; tiếp cận với một thế giới bao la b&aacute;t ng&aacute;t, mở ra trước mắt hắn một thế giới ho&agrave;n to&agrave;n mới mẻ.<br />\r\n<br />\r\n- Nhưng &quot;&Acirc;m&quot; v&agrave; &quot;Dương&quot; lu&ocirc;n kh&aacute;c biệt v&agrave; b&agrave;i x&iacute;ch lẫn nhau, người lu&ocirc;n gặp phải cảnh t&ugrave;y thời c&oacute; khả năng đ&aacute;nh mất tr&iacute; nhớ, h&oacute;a th&acirc;n th&agrave;nh &quot;Ma vật&quot; h&aacute;o s&aacute;t, dẫn đến nguy cơ s&aacute;t th&acirc;n... Truyện sẽ diễn biến ra sao, Liễu Minh, nh&acirc;n vật ch&iacute;nh của ch&uacute;ng ta đối mặt với những kh&oacute; khăn thử th&aacute;ch như thế n&agrave;o? Mời qu&yacute; độc giả đ&oacute;n đọc tiểu thuyết Ti&ecirc;n hiệp thứ hai của Vong Ngữ, với bố cục chặt chẽ, logic vốn c&oacute;, hứa hẹn tr&agrave;n ngập t&igrave;nh tiết l&ocirc;i cuốn v&agrave; mới vẻ: Ma Thi&ecirc;n K&yacute;.<br />\r\n&nbsp;</p>\r\n', '24871_ma-thien-ky.jpg', '85010_ma-thien-ky.jpg', 1, '3', 42, 0, 1),
(13, 1, 3, 0, 'Huyền Giới Chi Môn', '<p><strong><em>Nh&oacute;m dịch:</em></strong><em>Ph&agrave;m Nh&acirc;n T&ocirc;ng (đến 632)</em><br />\r\n<br />\r\nTrời gi&aacute;ng Thần vật! Dị huyết phụ thể!<br />\r\n<br />\r\nQuần Ti&ecirc;n sợ h&atilde;i! Vạn Ma tr&aacute;nh lui!<br />\r\n<br />\r\nMột thiếu ni&ecirc;n xuất&nbsp;th&acirc;n&nbsp;từ đại lục Đ&ocirc;ng Ch&acirc;u.<br />\r\n<br />\r\nMệnh sinh tử gắn liền với Hồng Phấn Kh&ocirc; L&acirc;u.<br />\r\n<br />\r\n&Yacute; ch&iacute; ki&ecirc;n định quyết trở th&agrave;nh đệ nhất cường giả.<br />\r\n<br />\r\nTạo n&ecirc;n truyền thuyết uy vũ khắp ng&acirc;n h&agrave;, đại n&aacute;o khắp ba c&otilde;i.</p>\r\n', '7694_huyen-gioi-chi-mon.jpg', '86170_huyen-gioi-chi-mon.jpg', 0, '0', 6, 0, 1),
(14, 1, 5, 1, 'Thôn Phệ Tinh Không', '<p>Đ&acirc;y l&agrave; 1 bộ truyện mới của c&agrave; chua thuộc thể loại Khoa Ảo mới mẻ . Nh&acirc;n vật ch&iacute;nh l&agrave; La Phong lu&ocirc;n muốn cố gắng trở th&agrave;nh 1 v&otilde; giả trong thế giới hiện đại tương lai đầy lạ lẫm , loại người đứng trước nguy cơ qu&aacute;i th&uacute;. Mở đầu c&acirc;u chuyện rất hấp dẫn l&ocirc;i cuốn , qua từng c&acirc;u văn người đọc sẽ tự nhận biết được bối cảnh của c&acirc;u chuyện .Nỗ lực , kh&aacute;t khao, c&oacute; t&igrave;nh c&oacute; nghĩa , lối h&agrave;nh văn vững chắc sẽ l&agrave;m bạn nếu đ&atilde; đọc liền k thể rời mắt , vẫn l&agrave; c&agrave; chua , h&atilde;y theo La Phong của ch&uacute;ng ta kh&aacute;m ph&aacute; ra thế giới v&ocirc; tận trong vũ trụ tinh kh&ocirc;ng nh&eacute;</p>\r\n', '23556_thon-phe-tinh-khong.jpg', '99753_thon-phe-tinh-khong.jpg', 4, '1', 243, 0, 1),
(15, 2, 5, 0, 'Mãng Hoang Kỷ', '<p>Giới Thiệu Truyệnrn<br />\r\n<br />\r\nMời c&aacute;c độc giả c&ugrave;ng theo d&otilde;i tiểu thuyết tu ti&ecirc;n huyền huyễn của t&aacute;c giả Ng&atilde; Cật T&acirc;y Hồng Thị:&nbsp;<em>M&atilde;ng Hoang Kỷ</em>.<br />\r\n<br />\r\n- Một đứa b&eacute; sinh ra đ&atilde; bị bệnh hiểm ngh&egrave;o, vừa hiểu được gi&aacute; trị của cuộc đời đ&atilde; phải đếm dần ng&agrave;y chết. C&ocirc; độc gặm nhấm nỗi đau mười t&aacute;m năm trời cuối c&ugrave;ng cũng kh&ocirc;ng thể tr&aacute;nh được số mệnh, người thanh ni&ecirc;n đ&oacute; m&atilde;i m&atilde;i ra đi.<br />\r\n<br />\r\n- Nhưng hắn đ&acirc;u ngờ chết đi lại l&agrave; một khởi đầu mới, l&agrave; một chuỗi nối tiếp nh&acirc;n quả của kiếp trước, v&agrave; l&agrave; cơ may cũng như một tương lai rạng ngời cho kiếp sống mới, lu&acirc;n hồi, đầu thai&hellip; những kh&aacute;i niệm m&agrave; người thanh ni&ecirc;n n&agrave;y chỉ gặp trong phim ảnh bỗng chốc h&oacute;a th&agrave;nh hiện thực&hellip;</p>\r\n', '17476_mang-hoang-ky.jpg', '12060_mang-hoang-ky.jpg', 1, '5', 4, 0, 1),
(16, 5, 5, 0, 'Tinh Thần Biến', '<p><strong><a href=\\\"https://truyenfull.vn/tinh-than-bien/\\\" target=\\\"_blank\\\">Tinh Thần Biến</a>&nbsp;l&agrave; một tiểu thuyết v&otilde; hiệp c&oacute; nội dung về một c&acirc;u&nbsp;<strong>Truyện Ti&ecirc;n Hiệp</strong>&nbsp;ho&agrave;nh tr&aacute;ng kể về người thanh ni&ecirc;n Tần Vũ gian khổ tu luyện, vượt hết kh&oacute; khăn n&agrave;y đến nguy hiểm kh&aacute;c để l&ecirc;n Thần Giới t&igrave;m người y&ecirc;u.</strong><br />\r\n<br />\r\n- Tần Vũ đ&atilde; gặp duy&ecirc;n kỳ ngộ học được Tinh Thần Biến c&ocirc;ng ph&aacute;p của L&ocirc;i Vệ, gian khổ tu luyện để l&agrave;m cho người th&acirc;n m&igrave;nh an to&agrave;n, nhờ sự gi&uacute;p sức của Lan th&uacute;c(ch&uacute; của người y&ecirc;u) v&agrave;&nbsp;<strong>Nghịch Ương ti&ecirc;n đế</strong>&nbsp;để phi thăng l&ecirc;n ti&ecirc;n giới v&agrave; thần giới, nhưng rồi tại thần giới tranh đấu với cả nh&agrave; thần vương của L&ocirc;i Phạt th&agrave;nh v&agrave; L&ocirc;i Phạt thi&ecirc;n t&ocirc;n, cuối c&ugrave;ng nhờ sự gi&uacute;p sức của L&ocirc;i M&ocirc;ng (sau n&agrave;y l&agrave; nhị sư huynh của Tần Vũ v&agrave; cũng l&agrave; L&acirc;m L&ocirc;i trong truyện B&agrave;n Long c&ugrave;ng t&aacute;c giả) ph&aacute;t triển l&ecirc;n sau n&agrave;y đ&atilde; s&aacute;ng tạo ra Tần M&ocirc;ng vũ trụ của ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p>-&nbsp;<em>Truyện Tinh Thần Biến</em>&nbsp;mang đậm sắc th&aacute;i ti&ecirc;n, thần, lấy tinh thần dũng cảm vượt kh&oacute; khăn, cố gắng đến cực hạn của bản th&acirc;n nh&acirc;n vật ch&iacute;nh để đi t&igrave;m được t&igrave;nh y&ecirc;u của m&igrave;nh.</p>\r\n', '55014_tinh-than-bien.jpg', '13781_tinh-than-bien.jpg', 1, '3', 399, 0, 1),
(17, 7, 5, 0, 'Thốn Mang', '<p><strong>Thốn Mang</strong><br />\r\n<br />\r\n- D&ugrave; g&acirc;y ra cơn sốt kh&ocirc;ng lớn bằng&nbsp;<strong>B&agrave;n Long</strong>, nhưng t&aacute;c phẩm cũng để lại một ấn tượng cho đọc giả. Đ&acirc;y l&agrave; một t&aacute;c phẩm của t&aacute;c giả&nbsp;<strong>Ng&atilde; Cật C&acirc;y Hồng Thị</strong>&nbsp;<em>(L&atilde;o C&agrave; Chua)</em>&nbsp;n&oacute;i về v&otilde; thuật mang hơi hương đ&ocirc; thị, với t&igrave;nh tiết hấp dẫn cũng sẽ l&agrave;m cho đọc giả ng&acirc;y ngất...<br />\r\n&nbsp;</p>\r\n', '52877_thon-mang.jpg', '12634_thon-mang.jpg', 1, '4', 17, 0, 1),
(18, 2, 8, 1, 'Thông Thiên Chi Lộ', '<p>Giới Thiệu Truyện<br />\r\n<br />\r\n- Th&ocirc;ng thi&ecirc;n lu&ocirc;n l&agrave; con đường mơ ước của những người&nbsp;<strong>tu ch&acirc;n</strong>&nbsp;từ bao đời. Tu kiếm, tu trận, tu bằng đơn dược hay song tu&hellip; tất cả đều hướng đến con đường l&agrave;&nbsp;<strong>th&ocirc;ng thi&ecirc;n</strong>, vươn tới trời cao.<br />\r\n<br />\r\n- Hắn, chỉ l&agrave; một t&aacute;n tu nhỏ nhoi trong ng&ocirc;i th&agrave;nh nhỏ, bị gạt gẫm mua đồ rởm nhưng v&ocirc; t&igrave;nh lại nhờ đ&oacute; bước l&ecirc;n con đường trở th&agrave;nh cường giả&nbsp;<strong>th&ocirc;ng thi&ecirc;n</strong>.<br />\r\n<br />\r\n- N&agrave;ng, l&agrave;&nbsp;<strong>đệ nhất mỹ nữ</strong>&nbsp;của đại lục, được v&ocirc; số thanh ni&ecirc;n t&agrave;i tuấn ngưỡng mộ nhưng rồi cuối c&ugrave;ng cũng về b&ecirc;n hắn. N&agrave;ng, l&agrave; mỹ nữ băng tuyết nổi danh, hết l&ograve;ng v&igrave; sư muội, nhờ duy&ecirc;n trời đưa đẩy m&agrave; đến b&ecirc;n hắn. N&agrave;ng l&agrave; đại tu sĩ lừng lẫy một phương, cũng v&igrave; một lần gặp gỡ m&agrave; bước c&ugrave;ng con đường với hắn.<br />\r\n<br />\r\n- H&atilde;y c&ugrave;ng đọc&nbsp;</p>\r\n\r\n<h3><a href=\\\"https://truyenfull.vn/thong-thien-chi-lo/\\\" target=\\\"_blank\\\">Th&ocirc;ng Thi&ecirc;n Chi Lộ</a></h3>\r\n\r\n<p>, để trải nghiệm con đường đi l&ecirc;n th&agrave;nh cường giả. Vẫn l&agrave; một&nbsp;<strong>thế giới tu ti&ecirc;n</strong>&nbsp;c&aacute; lớn nuốt c&aacute; b&eacute;, đầy những hiểm &aacute;c nhưng kh&ocirc;ng thiếu t&igrave;nh người, v&agrave; quan trọng l&agrave; ngập tiếng cười, chắc chắn sẽ khiến bạn thỏa m&atilde;n với những ph&uacute;t gi&acirc;y được thư gi&atilde;n. Đọc để cảm nhận ng&ograve;i b&uacute;t của&nbsp;<strong>V&ocirc; Tội</strong>, cao thủ viết&nbsp;<strong><a href=\\\"https://truyenfull.vn/\\\" target=\\\"_blank\\\">truyện ti&ecirc;n hiệp</a></strong>&nbsp;h&agrave;i hước nổi danh.</p>\r\n', '28100_thong-thien-chi-lo.jpg', '5403_thong-thien-chi-lo.jpg', 1, '3', 528, 0, 1);
